package P02;

public class 산술연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;
		int n2 = 3;
		
		System.out.println(n + n2);
		System.out.println(n - n2);
		System.out.println(n * n2);
		System.out.println(n / n2);
		System.out.println(n % n2);
		
		
		System.out.println(n / 3.0);
		
		
	}

}
